import { useState } from 'react';
import { Link, useHistory } from 'react-router-dom/cjs/react-router-dom.min';
import Signup from './Signup';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [valid, setValid] = useState(false);
  const history=useHisto

  return (
    <div className="login-container">
    <div className='login-header'>
      <h1>Login</h1>
      </div>
      <form
      className="login-form"
        onSubmit={(event) => {
          event.preventDefault();
          console.log(username);
          console.log(password);
          const UNList = JSON.parse(localStorage.getItem('UserN'));
          console.log(UNList);
          const PassList = JSON.parse(localStorage.getItem('PassW'));
          if (UNList.includes(username)) {
            const p = UNList.indexOf(username);
            if (PassList[p] == password) {
              console.log('True Credentials');
              setValid(true);
              history.pushState()
            } else {
              setError('Entered Wrong password');
            }
          } else {
            setError('Enter valid username');
          }
        }}
      >
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </label>
        <br />
        <button type="submit">Login</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button
          type="sign-up"
          onClick={() => (window.location.href = '/sign-up')}
        >
          Click to signup!
        </button>
      </form>
    </div>
  );
};

export default Login;
