import { useState } from 'react';
import { Link, useHistory } from 'react-router-dom/cjs/react-router-dom.min';
// localStorage.setItem('UserN', '["sai"]');
// localStorage.setItem('PassW', '["123"]');
localStorage.setItem('LogIn',JSON.stringify([{ UseN: 'sai', PassW: '123' }]))

const Signup = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const history = useHistory();

  return (
    <div className="login-container">
      <div className="login-header">
        <h1>SignUp</h1>
      </div>
      <form
        className="login-form"
        onSubmit={(event) => {
          event.preventDefault();
        //   const UNList = JSON.parse(localStorage.getItem('UserN'));
        //   const PassList = JSON.parse(localStorage.getItem('PassW'));
          const Login=JSON.parse(localStorage.getItem("LogIn"))
          console.log(Login,"Login info")

          console.log('CLicked');
          Login.forEach(element => {
            if(element.UseN==username)
            {
                setError("username already taken")
            }
        });
          if (errorusername !== '' && password !== '') {
            console.log('data set');
            Login.forEach(element => {
                if(element.UseN==username)
                {
                    setError("username already taken")
                }
            });
            if(error===null) Login.push({UserN:username,PassW:password})
            // UNList.push(username);
            // PassList.push(password);
            // localStorage.setItem('UserN', JSON.stringify(UNList));
            // localStorage.setItem('PassW', JSON.stringify(PassList));
            localStorage.setItem('LogIn',JSON.stringify(Login));
            //  window.location = '/home';
            history.push('/home');
          }
        }}
      >
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </label>
        <br />
        <label>
          Re-enter Password:
          <input
            type="repassword"
            onChange={(event) => {
              if (password !== event.target.value) {
                setError('Passwords dont match');
              } else {
                setError('');
              }
            }}
          />
        </label>
        <p>{error && <p style={{ color: 'red' }}>{error}</p>}</p>
        <>
        {error==='username already taken' && <button type="submit" style={{ padding: '8px 20px' }}>
          SignUp!
        </button>}
        </>
      </form>
    </div>
  );
};

export default Signup;
