import React, { useEffect, useMemo } from "react";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import { useState } from "react";
import InfiniteScroll from "react-infinite-scroll-component"

const ProductComponent=()=>{
    const product = useSelector((state)=>state.allproducts.products); //array of objects

    const searchText=useSelector((state)=>state.search.searchWord)

    const [filterData,setFilteredData] = useState([])

    const [count,setCount]=useState(0)

    const [state,setState] = useState({
      items: filterData.slice(0,1),
      hasMore: true
    });

    // useEffect=(()=>{
    //   setState(filterData)
    // },[filterData])

    const fetchMoreData = () => {
      if (state.items.length >= 20) {
        setState({ hasMore: false });
        return;
      }
      // a fake async api call like which sends
      // 20 more records in .5 secs
      setTimeout(() => {
        setState({
          items: state.items.concat(filterData.slice(count,4))
        });
        setCount(count+4)
      }, 10000);
    };
  
    // const searchText = 
  //  setFilteredData(product)
   useEffect(() => {
    if(searchText){
      //console.log("if")
      const filteredArray = product.filter(data => data.title.toLowerCase().includes(searchText.toLowerCase()))
      setFilteredData(filteredArray)
    }
    else {
      // console.log("else")
      // console.log(product)
      setFilteredData(product)
    }

   },[searchText,product])

