import { useState } from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";


const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit=()=>
  {
    console.log(use)
    const UNList = JSON.parse(
        localStorage.getItem('UserN') 
    );
    console.log(UNList)
    const PassList = JSON.parse(
        localStorage.getItem('PassW') 
    );
    if(UNList.includes(username))
    {
        const p=UNList.indexOf(username)
        if(PassList[p]==password)
        {
            <Link to={`/home`} />
        }
        else{
            setError("Entered Wrong password")
        }
    }
    else{
        setError("Enter valid username")
    }
  }
  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </label>
        <br />
        <button type="submit">Login</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="sign-up" onClick={`/sign-up`}>Click to signup!</button>
      </form>
    </div>
  );
};

export default Login;
