import { useState } from 'react';
import { Link, useHistory } from 'react-router-dom/cjs/react-router-dom.min';

localStorage.setItem('LogIn', JSON.stringify([{ UseN: 'sai', PassW: '123' }]));

const Signup = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [perror, setPerror] = useState('');
  const [uerror, setUerror] = useState('');
  const history = useHistory();

  return (
    <div className="login-container">
      <div className="login-header">
        <h1>SignUp</h1>
      </div>
      <form
        className="login-form"
        onSubmit={(event) => {
          event.preventDefault();

          const Login = JSON.parse(localStorage.getItem('LogIn'));
          console.log(Login, 'Login info');
          console.log('CLicked');
          console.log(typeof (Login[0].UseN), 'UseN');
          console.log(typeof (username), 'username');
          console.log(uerror);
          if (uerror==='' && perror === '' && username !== '' && password !== '') {
            console.log('data set');
            Login.push({ UserN: username, PassW: password });

            localStorage.setItem('LogIn', JSON.stringify(Login));

            history.push('/home');
          }
        }}
      >
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(event) => {
                const Login = JSON.parse(localStorage.getItem('LogIn'));
                setUsername(event.target.value)
                if (Login.some((element) => element.UseN === event.target.value)) {
                    console.log("found")
                    setUerror('Username already taken');
                }
                else{
                    setUerror('')
                }
            }}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </label>
        <br />
        <label>
          Re-enter Password:
          <input
            type="repassword"
            onChange={(event) => {
              if (password !== event.target.value) {
                setPerror('Passwords dont match');
              } else {
                setPerror('');
              }
            }}
          />
        </label>
        {/* <p>{error && <p style={{ color: 'red' }}>{error}</p>}</p> */}

        <button type="submit" style={{ padding: '8px 20px' }}>
          SignUp!
        </button>
        <p>{perror && uerror && <p style={{ color: 'red' }}>{rror}</p>}</p>
      </form>
    </div>
  );
};

export default Signup;
