import { useState } from 'react';
import { Link, useHistory } from 'react-router-dom/cjs/react-router-dom.min';
localStorage.setItem('UserN', '["sai"]');
localStorage.setItem('PassW', '["123"]');

const Signup = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [history,setHistory] = useHistory();

  const handleSubmit = () => {
    const UNList = JSON.parse(localStorage.getItem('UserN'));
    const PassList = JSON.parse(localStorage.getItem('PassW'));
    UNList.push(username);
    PassList.push(password);
    localStorage.setItem('UserN', JSON.stringify(UNList));
    localStorage.setItem('PassW', JSON.stringify(PassList));
    history.push()
  };
  return (
    <div className="login-container">
      <div className="login-header">
        <h1>SignUp</h1>
      </div>
      <form
        className="login-form"
        onSubmit={(event) => {
          event.preventDefault();
          const UNList = JSON.parse(localStorage.getItem('UserN'));
          const PassList = JSON.parse(localStorage.getItem('PassW'));
          console.log('CLicked');
          if (username !== '' && password !== '') {
            console.log('data set');
            console.log(UNList);
            UNList.push(username);
            PassList.push(password);
            localStorage.setItem('UserN', JSON.stringify(UNList));
            localStorage.setItem('PassW', JSON.stringify(PassList));
            //  window.location = '/home';
          }
        }}
      >
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </label>
        <br />
        <label>
          Re-enter Password:
          <input
            type="repassword"
            onChange={(event) => {
              if (password !== event.target.value) {
                setError('Passwords dont match');
              } else {
                setError('');
              }
            }}
          />
        </label>
        <p>{error && <p style={{ color: 'red' }}>{error}</p>}</p>
        <button type="submit">SignUp!</button>
      </form>
    </div>
  );
};

export default Signup;
